mapper.select:
select

from biz_egg_quotation


        and id=#{id,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}


        and egg_type_id=#{eggTypeId,jdbcType=BIGINT}


        and egg_type_name=#{eggTypeName,jdbcType=VARCHAR}


        and quotation_location=#{quotationLocation,jdbcType=VARCHAR}


        and quotation_max_price=#{quotationMaxPrice,jdbcType=DECIMAL}


        and quotation_avg_price=#{quotationAvgPrice,jdbcType=DECIMAL}


        and quotation_min_price=#{quotationMinPrice,jdbcType=DECIMAL}


        and quotation_status=#{quotationStatus,jdbcType=INTEGER}


        and quotation_type=#{quotationType,jdbcType=INTEGER}


        and data_date=#{dataDate,jdbcType=TIMESTAMP}


        and create_time=#{createTime,jdbcType=TIMESTAMP}


        and update_time=#{updateTime,jdbcType=TIMESTAMP}


        and is_delete=#{isDelete,jdbcType=INTEGER}
