mapper.select:
select

from biz_order


        and id=#{id,jdbcType=INTEGER}


        and order_no=#{orderNo,jdbcType=INTEGER}


        and good_id=#{goodId,jdbcType=INTEGER}


        and good_name=#{goodName,jdbcType=VARCHAR}


        and good_desc=#{goodDesc,jdbcType=VARCHAR}


        and total_price=#{totalPrice,jdbcType=VARCHAR}


        and school=#{school,jdbcType=VARCHAR}


        and phone=#{phone,jdbcType=VARCHAR}


        and `password`=#{password,jdbcType=VARCHAR}


        and class_info=#{classInfo,jdbcType=VARCHAR}


        and order_status=#{orderStatus,jdbcType=INTEGER}


        and create_time=#{createTime,jdbcType=TIMESTAMP}


        and pay_time=#{payTime,jdbcType=TIMESTAMP}


        and is_delete=#{isDelete,jdbcType=INTEGER}


        and version=#{version,jdbcType=INTEGER}
