mapper.select:
select

from biz_user


        and id=#{id,jdbcType=INTEGER}


        and phone=#{phone,jdbcType=VARCHAR}


        and wx_open_id=#{wxOpenId,jdbcType=VARCHAR}


        and wx_union_id=#{wxUnionId,jdbcType=VARCHAR}


        and nick_name=#{nickName,jdbcType=VARCHAR}


        and user_avatar=#{userAvatar,jdbcType=VARCHAR}


        and user_desc=#{userDesc,jdbcType=VARCHAR}


        and user_location=#{userLocation,jdbcType=VARCHAR}


        and user_status=#{userStatus,jdbcType=INTEGER}


        and create_time=#{createTime,jdbcType=TIMESTAMP}


        and update_time=#{updateTime,jdbcType=TIMESTAMP}


        and is_delete=#{isDelete,jdbcType=INTEGER}
